import React from 'react';
import { View, StyleSheet, Image, Pressable } from 'react-native';
import colors from '../styles/colors'
import { useNavigation } from '@react-navigation/native';
import { BoldAppText, MediumAppText } from '../styles/text'

function Footer () {

  const navigation = useNavigation();

// TODO stop someone being able to click on the screen they are already navigated to, otherwise maybe we create multiple items in stack which we dont want

  return (
    <View style={styles.container}>
      <View style={styles.wrapper}>
        <Pressable style={styles.icon}>
          <Image style={styles.icon} onPress={() => navigation.navigate('Home')} source={require('../assets/homelightgrey.png')}/>
        </Pressable>
        <BoldAppText style={styles.text}>Home Link</BoldAppText>
      </View>
      <View style={styles.wrapper}>
        <Pressable style={styles.icon}>
          <Image style={styles.icon} onPress={() => navigation.navigate('Summary')} source={require('../assets/dashboardlightgrey.png')}/>
        </Pressable>
        <BoldAppText style={styles.text}>Summary Link</BoldAppText>
      </View>
      <View style={styles.wrapper}>
        <Pressable onPress={() => navigation.navigate('Calendar')} source={require('../assets/datelightgrey.png')}>
          <Image style={styles.icon} />
        </Pressable>
        <BoldAppText style={styles.text} >Calendar Link</BoldAppText>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: colors.orange,
    position: 'absolute',
    width: '100%',
    height: 65,
    elevation:2,
    bottom: 0,
    borderTopColor: 'white',
    borderTopWidth: 2,
  },
  wrapper: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    fontSize: 12,
    color: colors.cosmicLatte
  },
  icon: {
    height: 25,
    width: 25,
  },
});

export default Footer;
