import React from 'react';
import { StyleSheet, View, Text, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useSelector } from "react-redux";

function SuggestionItem({ name, clickHandler, selected }) {

  const selectedStyling = '#FF55B8';
  const notSelectedStyling = '#BBBCCD';
  const suggestionList = useSelector((state) => state.settings.suggestionSettings.suggestionsList);

  return (
    <View style={styles.container}>
      <TouchableOpacity>
          <LinearGradient colors={lineargradient} style={styles.button}></LinearGradient>
      </TouchableOpacity>
      <Text>{name}</Text>
    </View>
  );
}

const lineargradient = ['#ADF1FF', '#FFF4E4']

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    width: 34,
    height: 34,
    borderRadius: 50,
    marginHorizontal: 20,
  },
});

export default SuggestionItem;
