import React from 'react';
import { StyleSheet, View, Text, TouchableOpacity } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useSelector } from 'react-redux';
import colors from '../styles/colors'

function SuggestionItem({ name, clickHandler, selected }) {

  const dailySuggestions = useSelector((state) => state.dailyInfo.suggestions)

  return (
    <View style={styles.container}>
      <TouchableOpacity onPress={() => {clickHandler(dailySuggestions, name)}}>
          <LinearGradient colors={selected ? selectedStyling : notSelectedStyling} style={selected? styles.selectedButton : styles.unselectedButton}>
          </LinearGradient>
      </TouchableOpacity>
      <Text>{name}</Text>
    </View>
  );
}

const notSelectedStyling = [colors.cosmicLatte, colors.lightGray]
const selectedStyling = ['#FF8787', '#FF55B8']

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  unselectedButton: {
    width: 25,
    height: 25,
    borderRadius: 50,
    marginHorizontal: 20,
  },
  selectedButton: {
    width: 30,
    height: 30,
    borderRadius: 50,
    marginHorizontal: 20,
  },
});

export default SuggestionItem;
