import React from 'react';
import { StyleSheet, View, Text, Button, TouchableOpacity } from 'react-native';
import { useSelector } from "react-redux";
import colors from '../styles/colors'
import { BoldAppText, MediumAppText } from '../styles/text'

function ModifySuggestionItem ({name, addTick, selected}) {

  const selectedStyling = colors.darkGrayFont;
  const notSelectedStyling = colors.green;
  const selectedOpacity = 0.5;
  const unselectedOpacity = 1;

  const suggestionList = useSelector((state) => state.settings.suggestionSettings.suggestionsList);

  return (
    <View style={styles.container}>
      <TouchableOpacity color={selected ? selectedStyling : notSelectedStyling} marginTop={0} opacity={selected? selectedOpacity: unselectedOpacity} onPress={() => addTick(suggestionList ,name)}>
        <MediumAppText stlye={{margin:0}} >{name}</MediumAppText>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 5,
    marginBottom: 5,
  },
});

export default ModifySuggestionItem;
