import React, { useState } from 'react';
import { StyleSheet, View, Text, ScrollView } from 'react-native';
import { useSelector, useDispatch } from "react-redux";
import MeetingsSummary from '../components/MeetingsSummary'
import FeelingGraph from '../components/graphs/FeelingGraph'
import MoodVisuals from '../components/graphs/MoodVisuals'
import SuggestionsVisuals from '../components/graphs/SuggestionsVisuals'
import Footer from '../components/Footer'
import { Dimensions } from "react-native";
import Constants from 'expo-constants';
import { MediumAppText } from '../styles/text';
import colors from '../styles/colors';
import { TouchableOpacity } from 'react-native-gesture-handler';


function SummaryScreen () {

  const windowHeight = Dimensions.get('window').height;
  const statusBarHeight = Constants.statusBarHeight
  const fullHeight = windowHeight + statusBarHeight;

  const dynamicStyle = {
    height: fullHeight,
    paddingTop: statusBarHeight,
  };

  const chartHeight = {
    height: fullHeight/4,
  };

  const dispatch = useDispatch();
  const chartTimePeriod = useSelector((state) => state.helper.chartTimePeriod)

  //TODO dispatch this to helper reducer so we have access in all the charts

  return (
    <View style={[styles.container,  dynamicStyle]}>
      <ScrollView style={styles.scrollView}>
        <View style={styles.options}>
          {/* add option to show data for a week, month or alltime */}
          <View >
            <MediumAppText style={chartTimePeriod === 'week' ? styles.optionsTextSelected : styles.optionsTextUnselected}>WEEK</MediumAppText>
          </View>
          <View>
            <MediumAppText style={chartTimePeriod === 'month' ? styles.optionsTextSelected : styles.optionsTextUnselected}>MONTH</MediumAppText>
          </View>
          <View >
            <MediumAppText style={chartTimePeriod === 'all-time' ? styles.optionsTextSelected : styles.optionsTextUnselected}>ALL-TIME</MediumAppText>
          </View>
        </View>
        <View>
          <View style={styles.textWrapper}>
            <View>
              <Text>Days in recovery</Text>
            </View>
            <View>
              <MeetingsSummary />
            </View>
          </View>
          <View styles={chartHeight}>
            <FeelingGraph />
          </View>
          <View styles={chartHeight}>
            <MoodVisuals />
          </View>
          <View styles={chartHeight}>
            <SuggestionsVisuals/>
          </View>
        </View>
      </ScrollView>
      <Footer/>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: colors.platinum,
    flex: 1,
    paddingBottom: 55,
  },
  scrollView: {
    borderRadius: 5,
    backgroundColor: 'white',
    elevation: 1,
    margin: 10,
    paddingHorizontal: 10,
    paddingVertical: 12,
  },
  options: {
    flex: 1,
    flexDirection: 'row',
    alignSelf: 'flex-end',
    marginRight: 10,
  },
  optionsTextSelected: {
    marginTop: -3,
    marginRight: 7,
    borderBottomWidth: 2,
    borderColor: colors.blue,
    fontFamily: 'Montserrat_700Bold',
    color: colors.darkGrayFont,
    paddingBottom: 2,
  },
  optionsTextUnselected: {
    marginTop: -3,
    marginRight: 7,
    opacity: 0.5,
    fontFamily: 'Montserrat_500Medium',
    paddingBottom: 2,
  },
  charts: {

  },

});



export default SummaryScreen;
