const initialState = {
  meetings: 2,
  feeling: false,
  moods: [],
  suggestions: ['placeholder', 'second'],
}

const dailyInfoReducer = (state = initialState, action) => {
  switch (action.type) {
    case "INCREMENT_DAILY_MEETINGS":
      return {
        ...state,
        meetings: state.meetings +1
      }
    case "DECREMENT_DAILY_MEETINGS":
      return {
        ...state,
        meetings: state.meetings -1
      }
    case "UPDATE_FEELING":
      return {
        ...state,
        feeling: action.payload
      }
    case "ADDTO_SUGGESTIONS":
      return {
        ...state,
        dailyInfo: {
          ...state.dailyInfo,
          suggestions: [...state.dailyInfo.suggestions, action.payload]
        }
      }
    case "REMOVEFROM_SUGGESTIONS":
    return {
      ...state,
      dailyInfo: {
        ...state.dailyInfo,
        suggestions: state.dailyInfo.suggestions.filter(elInList => elInList !== action.payload)
      }
    }
    default:
      return state;
   }
}

export default dailyInfoReducer;
